INSERT INTO autores (qtd_livros, biografia, status_atividade, data_falecimento, id_pessoa_fk)
	VALUES
		('2', 'Autora misteriosa, tecendo palavras com maestria, revelando mundos inexplorados. Sua escrita encanta corações, desvenda mistérios e ilumina mentes sedentas. Um talento indescritível.', 'ativo', null, '1'),
        ('3', 'Escritor apaixonado pela arte das palavras. Suas histórias encantam leitores de todas as idades, transportando-os para mundos imaginários repletos de aventuras e emoções.', 'aposentado', null, '4'),
		('1', 'Autora visionária, trazendo à tona questões sociais e filosóficas através de suas narrativas profundas. Seus livros inspiram reflexões e debates importantes.', 'ativo', null, '5'),
		('4', 'Escritora rebelde, desafiando padrões e tabus em suas obras provocativas. Sua coragem em abordar temas controversos deixa uma marca indelével na literatura.', 'ativo', null, '7'),
		('1', 'Autor misterioso e enigmático, sempre se ocultando por trás de pseudônimos. Seus livros despertam a curiosidade dos leitores, que tentam desvendar sua verdadeira identidade.', 'ativo', null, '10'),
		('2', 'Escritor inovador, explorando novas formas de narrativa e linguagem. Sua prosa poética cativa os leitores, levando-os a uma jornada única de imaginação.', 'ativo', null, '12'),
		('5', 'Autora emocional, transmitindo sentimentos profundos através de suas palavras. Suas histórias tocam o coração dos leitores, conectando-os com suas próprias emoções.', 'ativo', null, '17'),
		('2', 'Escritora observadora, retratando a vida cotidiana com precisão e sensibilidade. Seus livros capturam a essência humana, revelando a beleza e complexidade da existência.', 'ativo', null, '21'),
		('1', 'Autora romântica, descrevendo amores intensos e sonhos impossíveis. Suas histórias envolventes fazem os leitores suspirarem e acreditarem no poder do amor verdadeiro.', 'falecido', '2022-02-05', '23'),
		('1', 'Escritor aventureiro, viajando pelo mundo e coletando experiências para suas narrativas empolgantes. Seus livros transportam os leitores para terras distantes e aventuras emocionantes.', 'ativo', null, '27'),
		('3', 'Autor visionário, explorando cenários futurísticos e distópicos. Suas histórias provocam reflexões sobre a sociedade e o impacto da tecnologia na vida humana.', 'ativo', null, '31');
